mapper.update:
UPDATE t_store_good


        store_id = CASE id

            WHEN #{item.id} THEN #{item.storeId}

        END,


        good_id = CASE id

            WHEN #{item.id} THEN #{item.goodId}

        END,


        nums = CASE id

            WHEN #{item.id} THEN #{item.nums}

        END,


        price_in_cent = CASE id

            WHEN #{item.id} THEN #{item.priceInCent}

        END,


        name = CASE id

            WHEN #{item.id} THEN #{item.name}

        END,


        ext_info = CASE id

            WHEN #{item.id} THEN #{item.extInfo}

        END,

    update_time = NOW()

WHERE id IN

    #{item.id}
